mapper.select:
		SELECT MEMBER_EMAIL , MEMBER_NAME,MEMBER_NICKNAME,MEMBER_PHONE,MEMBER_GENDER,MEMBER_BIRTH
		FROM TBL_MEMBER
		WHERE MEMBER_EMAIL = #{memberEmail}
      		AND MEMBER_NAME = #{memberName}
     		AND MEMBER_NICKNAME = #{memberNickName}
      		AND MEMBER_PHONE = #{memberPhone}
      		AND MEMBER_GENDER = #{memberGender}
      		AND MEMBER_BIRTH = #{memberBirth}

SELECT count(MEMBER_Name)
FROM TBL_MEMBER
WHERE MEMBER_EMAIL = #{memberEmail} AND MEMBER_PW = #{memberPw}

SELECT COUNT(MEMBER_NICKNAME)
FROM TBL_MEMBER
WHERE MEMBER_NICKNAME = #{memberNickName}

SELECT member_number, member_nickname, member_email, member_name, member_phone, member_gender, member_birth, member_pw
FROM tbl_member
WHERE member_number = #{memberNumber}
